    async Task ClickEvent()
    {
        if (click)
            return;
        click = true;
        if (ganaPierde)
            await Reiniciar();
        await Iniciar();
        click = false;
    }

    public async Task Iniciar()
    {
        if (ganaPierde)
            return;
        await _gameManager.VisibilidadBubble(true, "dado-gira");
        await _gameManager.Avanzar();
